// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function makeDir(path) {
  $.NSFileManager.defaultManager.createDirectoryAtPathWithIntermediateDirectoriesAttributesError(
    path, true, undefined, undefined)
}

function writeFile(path, text) {
  $(text).writeToFileAtomicallyEncodingError(path, true, $.NSUTF8StringEncoding, undefined)
}

function mv(initPath, targetPath) {
  $.NSFileManager.defaultManager.moveItemAtPathToPathError(initPath, targetPath, undefined)
}

function copyFile(initPath, targetPath) {
  $.NSFileManager.defaultManager.copyItemAtPathToPathError(initPath, targetPath, undefined)
}

function padDate(number) {
  return number.toString().padStart(2, "0")
}

// Constants for archive file name
const uid = $.NSProcessInfo.processInfo.globallyUniqueString.js.split("-")[0]
const currentDate = new Date()
const currentYear = currentDate.getFullYear()
const currentMonth = padDate(currentDate.getMonth() + 1) // Months are zero-based
const currentDay = padDate(currentDate.getDate())
const currentHour = padDate(currentDate.getHours())
const currentMinute = padDate(currentDate.getMinutes())
const currentSecond = padDate(currentDate.getSeconds())

// Main
const currentChat = `${envVar("alfred_workflow_data")}/chat.json`
const replacementChat = envVar("replace_with_chat")
const archiveDir = `${envVar("alfred_workflow_data")}/archive`
const archivedChat = `${archiveDir}/${currentYear}.${currentMonth}.${currentDay}.${currentHour}.${currentMinute}.${currentSecond}-${uid}.json`
const userSaveDir = envVar("chat_history_save_dir") // 用户指定的保存目录

makeDir(archiveDir)
mv(currentChat, archivedChat)

// 如果用户设置了保存目录，则额外复制一份到用户指定目录
if (userSaveDir && userSaveDir.length > 0) {
  makeDir(userSaveDir)
  const userArchivedChat = `${userSaveDir}/${currentYear}.${currentMonth}.${currentDay}.${currentHour}.${currentMinute}.${currentSecond}-${uid}.json`
  copyFile(archivedChat, userArchivedChat)
}

if (replacementChat) {
  mv(replacementChat, currentChat)
} else {
  writeFile(currentChat, "[]")
}

// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function readChat(path) {
  const chatString = $.NSString.stringWithContentsOfFileEncodingError(path, $.NSUTF8StringEncoding, undefined).js
  return JSON.parse(chatString)
}

// Main
function run() {
  const chatFile = `${envVar("alfred_workflow_data")}/chat.json`
  return readChat(chatFile).findLast(message => message["role"] === "assistant")["content"]
}

// Helpers
function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}


function readChat(path) {
  const chatString = $.NSString.stringWithContentsOfFileEncodingError(path, $.NSUTF8StringEncoding, undefined).js
  return JSON.parse(chatString)
}

function markdownChat(messages, ignoreLastInterrupted = true) {
  return messages.reduce((accumulator, current, index, allMessages) => {
    if (current["role"] === "assistant")
      return `${accumulator}${current["content"]}\n\n`

    if (current["role"] === "user") {
      const userMessage = `## You\n\n${current["content"]}\n\n## Assistant`
      const userTwice = allMessages[index + 1]?.["role"] === "user" // "user" role twice in a row
      const lastMessage = index === allMessages.length - 1 // "user is last message

      return userTwice || (lastMessage && !ignoreLastInterrupted) ?
        `${accumulator}${userMessage}\n\n[Answer Interrupted]\n\n` :
        `${accumulator}${userMessage}\n\n`
    }

    // Ignore any other role
    return accumulator
  }, "")
}

// Main
function run() {
  const chatFile = `${envVar("alfred_workflow_data")}/chat.json`
  return markdownChat(readChat(chatFile), false)
}

function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function dirContents(path) {
  return $.NSFileManager.defaultManager.contentsOfDirectoryAtURLIncludingPropertiesForKeysOptionsError(
    $.NSURL.fileURLWithPath(path), undefined, $.NSDirectoryEnumerationSkipsHiddenFiles, undefined)
    .js.map(p => p.path.js).sort()
}

function readChat(path) {
  const chatString = $.NSString.stringWithContentsOfFileEncodingError(path, $.NSUTF8StringEncoding, undefined).js
  return JSON.parse(chatString)
}

function trashChat(path) {
  const fileURL = $.NSURL.fileURLWithPath(path)
  $.NSFileManager.defaultManager.trashItemAtURLResultingItemURLError(fileURL, undefined, undefined)
}

function noArchives() {
  return JSON.stringify({ items: [{
    title: "历史对话记录为空",
    subtitle: "新会话开启后将会保存上一次（N轮）的历史对话",
    valid: false
  }]})
}

function run() {
  const archiveDir = `${envVar("alfred_workflow_data")}/archive`
  if (!$.NSFileManager.defaultManager.fileExistsAtPath(archiveDir)) return noArchives()

  const sfItems = dirContents(archiveDir)
    .filter(file => file.endsWith(".json"))
    .toReversed()
    .flatMap(file => {
      const chatContents = readChat(file)
      const firstQuestion = chatContents.find(item => item["role"] === "user")?.["content"]
      const lastQuestion = chatContents.toReversed().find(item => item["role"] === "user")?.["content"]

      // Delete invalid chats
      if (!firstQuestion) trashChat(file)

      return {
        type: "file",
        title: "历史提问：" + firstQuestion,
        subtitle: "最后一个问题是：" + lastQuestion,
        match: `${firstQuestion} ${lastQuestion}`,
        arg: file
      }
    })

  if (sfItems.length === 0) return noArchives()

  return JSON.stringify({ items: sfItems })
}

#!/usr/bin/osascript -l JavaScript

function envVar(varName) {
  return $.NSProcessInfo
    .processInfo
    .environment
    .objectForKey(varName).js
}

function dirContents(path) {
  return $.NSFileManager.defaultManager.contentsOfDirectoryAtURLIncludingPropertiesForKeysOptionsError(
    $.NSURL.fileURLWithPath(path), undefined, $.NSDirectoryEnumerationSkipsHiddenFiles, undefined)
    .js.map(p => p.path.js).sort()
}

function deleteFile(path) {
  $.NSFileManager.defaultManager.removeItemAtPathError(path, undefined)
}

function run() {
  const archiveDir = `${envVar("alfred_workflow_data")}/archive`

  // 检查archive目录是否存在
  if (!$.NSFileManager.defaultManager.fileExistsAtPath(archiveDir)) {
    return JSON.stringify({ 
      items: [{
        title: "归档目录不存在",
        subtitle: "No chat history files were deleted"
      }]
    })
  }

  // 获取目录中的所有文件
  const files = dirContents(archiveDir)
    .filter(file => file.endsWith(".json"))

  // 删除所有文件
  let deletedCount = 0
  files.forEach(file => {
    try {
      deleteFile(file)
      deletedCount++
    } catch (error) {
      // 忽略删除失败的文件
      console.log("Failed to delete file: " + file)
    }
  })

  // 返回结果
  return JSON.stringify({ 
    items: [{
      title: "成功清空历史会话记录",
      subtitle: `已删除 ${deletedCount} 条记录`
    }]
  })
}
